<script>
export default {
    data() {
        return {
            index: 0
        }
    },
    methods: {
        newArrivalLeftButton() {
            const movies = document.getElementsByClassName("new-arrival-movie-card")
            let movie = movies[this.index - 1]
            movie.style.display = "block"
            this.index--
        },

        newArrivalRightButton() {
            const movies = document.getElementsByClassName("new-arrival-movie-card")
            if (this.index < document.getElementsByClassName("new-arrival-movie-card").length - 1) {
                let movie = movies[this.index]
                movie.style.display = "none"
                this.index++
            }
        }
    }
}
